// TODO:
// 1) Add "Done" and "Remove" buttons to each TODO item 
// 2) Add a count to number of TODOs
// 3) Default clear input box after add
// 4) Default focus after add
// 5) Disable/warning when empty item is added 


import React, { useState } from "react";

function Form() {

    const [list, setList] = useState([]);
    const [input, setInput] = useState("");

    function handleInput(e) {
        setInput(e.target.value);
    }

    function handleList(e) {
        e.preventDefault();
        setList(item => [...item, input]);
        // setInput("");
        console.log(test);
    }

    return (<form>

        <input onChange={handleInput} name="todo-input" placeholder="Add TODO here"></input>
        <button onClick={handleList}>Add Item</button>
        <p>{list.map((i) => (<div><span>{i}</span><hr /></div>))}</p>
        <p>0 left to do</p>
    </form>);

}

export default Form;
